def get_floor(string)
  array = string.split("")
  floor = 0;

  array.each do |char|

    if char == "("
      floor += 1

    elsif char == ")"
      floor -= 1
    end
  end

  floor
end

def get_basement_entry(string)
  array = string.split("")
  floor = 0;

  array.each_with_index do |char, index|
    if char == "("
      floor += 1

    elsif char == ")"
      floor -= 1
    end

    if floor == -1
      return (index + 1)
    end
  end

end


